// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import Speedometer from './Speedometer'; // Seu componente de veloc√≠metro
// Seus outros componentes (SensorDisplay, AlertDisplay) permanecem aqui
const SensorDisplay = ({ label, value, unit, alert }) => (
  <div className={`sensor ${alert ? 'alert' : ''}`}>
    <span className="sensor-label">{label}:</span>
    <span className="sensor-value">{value !== undefined && value !== null && !isNaN(value) ? Number(value).toFixed(1) : 'N/A'} {unit}</span>
  </div>
);

const AlertDisplay = ({ message, active }) => (
  active ? <div className="alert-message active">{message}</div> : null
);


function App() {
  const [carData, setCarData] = useState({});
  const [isConnected, setIsConnected] = useState(false);
  const [error, setError] = useState(null);

  // --- Novos estados para o limite de velocidade ---
  const [speedLimitInput, setSpeedLimitInput] = useState(''); // Valor atual do campo de input
  const [customSpeedLimit, setCustomSpeedLimit] = useState(null); // Limite definido pelo usu√°rio
  const [showSpeedLimitAlert, setShowSpeedLimitAlert] = useState(false); // Controla exibi√ß√£o do alerta
  // --- Fim dos novos estados ---

  useEffect(() => {
    const wsUrl = 'ws://localhost:8765';
    const ws = new WebSocket(wsUrl);

    ws.onopen = () => console.log('Conectado ao servidor WebSocket');
    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        // console.log('Dados recebidos do WebSocket:', data); // Mantenha para debug
        setCarData(data);
      } catch (e) {
        console.error('Erro ao processar mensagem do WebSocket:', e);
      }
    };
    ws.onerror = (err) => console.error('Erro no WebSocket:', err);
    ws.onclose = () => console.log('Desconectado do servidor WebSocket');

    return () => {
      if (ws.readyState === WebSocket.OPEN || ws.readyState === WebSocket.CONNECTING) {
        ws.close();
      }
    };
  }, []);

  // --- Nova fun√ß√£o para lidar com a mudan√ßa no input do limite ---
  const handleSpeedLimitInputChange = (event) => {
    setSpeedLimitInput(event.target.value);
  };

  // --- Nova fun√ß√£o para definir o limite de velocidade ---
  const handleSetCustomSpeedLimit = () => {
    const newLimit = parseFloat(speedLimitInput);
    if (!isNaN(newLimit) && newLimit > 0) {
      setCustomSpeedLimit(newLimit);
      setSpeedLimitInput(''); // Limpa o input ap√≥s definir
      console.log(`Limite de velocidade definido para: ${newLimit} km/h`);
    } else {
      alert("Por favor, insira um valor v√°lido para o limite de velocidade.");
      setCustomSpeedLimit(null); // Reseta se inv√°lido
    }
  };

  // --- useEffect para verificar o excesso de velocidade ---
  useEffect(() => {
    // Certifique-se que carData.velocidade existe e √© um n√∫mero
    const currentSpeed = parseFloat(carData.velocidade);

    if (customSpeedLimit !== null && !isNaN(currentSpeed)) {
      if (currentSpeed > customSpeedLimit) {
        setShowSpeedLimitAlert(true);
      } else {
        setShowSpeedLimitAlert(false);
      }
    } else {
      setShowSpeedLimitAlert(false); // Sem limite definido ou sem dados de velocidade
    }
  }, [carData.velocidade, customSpeedLimit]); // Executa quando a velocidade ou o limite mudam


  return (
    <div className="car-multimedia-app">
      <header>
        <h1>Central Multim√≠dia Simulated</h1>
        <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
          {isConnected ? 'Conectado' : 'Desconectado'}
        </div>
        {error && <div className="error-message">{error}</div>}
      </header>

      <main className="dashboard">
        {/* --- Se√ß√£o para definir o limite de velocidade --- */}
        <section className="speed-limit-control">
          <h2>Definir Limite de Velocidade</h2>
          <div className="input-group">
            <input
              type="number"
              value={speedLimitInput}
              onChange={handleSpeedLimitInputChange}
              placeholder="Ex: 80"
              min="0"
            />
            <button onClick={handleSetCustomSpeedLimit}>Definir</button>
          </div>
          {customSpeedLimit !== null && (
            <p className="current-limit-text">Limite atual: {customSpeedLimit} km/h</p>
          )}
        </section>
        {/* --- Fim da se√ß√£o --- */}

        <section className="primary-info">
          {/* Lembre-se de que carData.velocidade precisa estar chegando corretamente */}
          <Speedometer speed={carData.velocidade} />
          <SensorDisplay label="Dist√¢ncia Frontal" value={carData.distancia_cm} unit="cm" alert={carData.alerta_distancia} />
        </section>

        <section className="engine-status">
          <h2>Motor & Fluidos</h2>
          <SensorDisplay label="N√≠vel Combust√≠vel" value={carData.combustivel_percent} unit="%" alert={carData.alerta_combustivel} />
          <SensorDisplay label="Temp. Motor" value={carData.temperatura_c} unit="¬∞C" />
          <SensorDisplay label="N√≠vel √ìleo" value={carData.nivel_oleo} unit="%" />
          <SensorDisplay label="Fluido Freio" value={carData.fluido_freio_percent} unit="%" alert={carData.alerta_fluido_freio} />
        </section>

        <section className="tire-status">
          <h2>Pneus</h2>
          <SensorDisplay label="Press√£o Pneus" value={carData.pressao_pneus} unit="PSI" />
        </section>

        <section className="alerts-panel">
          <h2>Alertas</h2>
          {/* --- Novo Alerta de Velocidade --- */}
          <AlertDisplay 
            message={`‚ö†Ô∏è ALERTA: Velocidade (${Number(carData.velocidade || 0).toFixed(0)} km/h) acima do limite de ${customSpeedLimit} km/h!`} 
            active={showSpeedLimitAlert} 
          />
          {/* --- Fim do Novo Alerta --- */}
          <AlertDisplay message="‚ö†Ô∏è ALERTA: Objeto pr√≥ximo!" active={carData.alerta_distancia} />
          <AlertDisplay message="‚õΩ ALERTA: Combust√≠vel baixo!" active={carData.alerta_combustivel} />
          <AlertDisplay message="üõë ALERTA: Fluido de freio baixo!" active={carData.alerta_fluido_freio} />
        </section>

        <section className="raw-data">
          <h2>Dados Crus (Debug)</h2>
          <pre>{JSON.stringify(carData, null, 2)}</pre>
        </section>
      </main>

      <footer>
        <p>Simula√ß√£o Wokwi & React</p>
      </footer>
    </div>
  );
}

export default App;
