// src/Speedometer.js
import React from 'react';
import GaugeChart from 'react-gauge-chart';

const Speedometer = ({ speed }) => {
  const maxSpeed = 220; // Velocidade máxima definida no seu Wokwi

  // O GaugeChart espera um valor percentual (0 a 1)
  // Certifique-se que 'speed' é um número antes de dividir
  const currentSpeed = (typeof speed === 'number' && !isNaN(speed)) ? speed : 0;
  const percent = currentSpeed / maxSpeed;

  const chartStyle = {
    height: 200, // Altura do velocímetro
    width: 300,  // Largura do velocímetro
    // Você pode ajustar outras propriedades de estilo aqui se necessário
  };

  return (
    <div className="speedometer-container">
      <h3>Velocidade</h3>
      <GaugeChart
        id="speedometer-gauge"
        style={chartStyle}
        nrOfLevels={22} // Número de "marcas" no velocímetro (ex: 22 para ir de 10 em 10 até 220)
        arcsLength={[0.4, 0.2, 0.4]} // Proporção das cores (verde, amarelo, vermelho)
        colors={['#5BE12C', '#F5CD19', '#EA4228']} // Cores: Verde, Amarelo, Vermelho
        percent={percent > 1 ? 1 : (percent < 0 ? 0 : percent)} // Garante que o percentual fique entre 0 e 1
        arcPadding={0.02}
        cornerRadius={3}
        textColor="#FFFFFF" // Cor do texto que mostra o valor
        needleColor="#E0E0E0"
        needleBaseColor="#C0C0C0"
        hideText={false} // Se true, esconde o texto com o valor percentual. Usaremos formatTextValue.
        formatTextValue={value => `${currentSpeed.toFixed(0)} km/h`} // Mostra a velocidade real
      />
    </div>
  );
};

export default Speedometer;
